function dPhidt = phiVelocity(vx, vy, vz, Px, Py, Pz, L1, L2)
    dPhidx = - (Px ./ (L1 .* (Px .^ 2 + Py .^ 2 + Pz .^ 2) .^ 0.5000) - (0.5000 .* Px .* (L1 .^ 2 - L2 .^ 2 + Px .^ 2 + Py .^ 2 + Pz .^ 2)) ./ (L1 .* (Px .^ 2 + Py .^ 2 + Pz .^ 2) .^ 1.5000)) ./ (1 - (0.2500 .* (L1 .^ 2 - L2 .^ 2 + Px .^ 2 + Py .^ 2 + Pz .^ 2) .^ 2) ./ (L1 .^ 2 .* (Px .^ 2 + Py .^ 2 + Pz .^ 2))) .^ 0.5000 - (Px .* Pz) ./ ((Pz .^ 2 ./ (Px .^ 2 + Py .^ 2) + 1) .* (Px .^ 2 + Py .^ 2) .^ 1.5000)
    dPhidy = - (Py ./ (L1 .* (Px .^ 2 + Py .^ 2 + Pz .^ 2) .^ 0.5000) - (0.5000 .* Py .* (L1 .^ 2 - L2 .^ 2 + Px .^ 2 + Py .^ 2 + Pz .^ 2)) ./ (L1 .* (Px .^ 2 + Py .^ 2 + Pz .^ 2) .^ 1.5000)) ./ (1 - (0.2500 .* (L1 .^ 2 - L2 .^ 2 + Px .^ 2 + Py .^ 2 + Pz .^ 2) .^ 2) ./ (L1 .^ 2 .* (Px .^ 2 + Py .^ 2 + Pz .^ 2))) .^ 0.5000 - (Py .* Pz) ./ ((Pz .^ 2 ./ (Px .^ 2 + Py .^ 2) + 1) .* (Px .^ 2 + Py .^ 2) .^ 1.5000)
    dPhidz = 1 ./ ((Pz .^ 2 ./ (Px .^ 2 + Py .^ 2) + 1) .* (Px .^ 2 + Py .^ 2) .^ 0.5000) - (Pz ./ (L1 .* (Px .^ 2 + Py .^ 2 + Pz .^ 2) .^ 0.5000) - (0.5000 .* Pz .* (L1 .^ 2 - L2 .^ 2 + Px .^ 2 + Py .^ 2 + Pz .^ 2)) ./ (L1 .* (Px .^ 2 + Py .^ 2 + Pz .^ 2) .^ 1.5000)) ./ (1 - (0.2500 .* (L1 .^ 2 - L2 .^ 2 + Px .^ 2 + Py .^ 2 + Pz .^ 2) .^ 2) ./ (L1 .^ 2 .* (Px .^ 2 + Py .^ 2 + Pz .^ 2))) .^ 0.5000

    dPhidt = dPhidx .* vx + dPhidy .* vy + dPhidz .* vz;
end